mapper.select:
SELECT
id,
user_account,
user_password,
user_name,
user_avatar,
user_profile,
user_role,
edit_time,
create_time,
update_time,
vip_expire_time,
vip_code,
vip_number,
share_code,
invite_user,
is_delete
FROM
user_table


    AND id = #{id}


    AND user_account = #{userAccount}


    AND user_password = #{userPassword}


    AND user_name = #{userName}


    AND user_avatar = #{userAvatar}


    AND user_profile = #{userProfile}


    AND user_role = #{userRole}


    AND edit_time = #{editTime}


    AND create_time = #{createTime}


    AND update_time = #{updateTime}


    AND vip_expire_time = #{vipExpireTime}


    AND vip_code = #{vipCode}


    AND vip_number = #{vipNumber}


    AND share_code = #{shareCode}


    AND invite_user = #{inviteUser}


    AND is_delete = #{isDelete}
